 //===============================================================================  var _oJQGFormatters = {   //===============================================================================   // FormatLocalDateTime function - dependant on Globalize library   //===============================================================================   FormatLocalDateTime: function (sCellValue, oOptions, oRow) {      if ($.trim(sCellValue).length === 0)       return "&nbsp;";     else {        //var dteDate = new Date(parseInt(sCellValue, 10)); JSON Date       var aDate = sCellValue.split("|");       var dteDate = new Date(aDate[0], aDate[1], aDate[2], aDate[3], aDate[4], aDate[5], aDate[6]);        return Globalize.format(dteDate, Globalize.culture().calendar.patterns["d"] + " " + Globalize.culture().calendar.patterns["t"]);     }   },    //===============================================================================   // FormatLocalDate function - dependant on Globalize library   //===============================================================================   FormatLocalDate: function (sCellValue, oOptions, oRow) {      if ($.trim(sCellValue).length == 0)       return "&nbsp;";     else {        //var dteDate = new Date(parseInt(sCellValue, 10)); //JSON Date       var aDate = sCellValue.split("|");       var dteDate = new Date(aDate[0], aDate[1], aDate[2]);        return Globalize.format(dteDate, Globalize.culture().calendar.patterns["d"]);     }   },    //===============================================================================   // FormatText function    //   - only necessary for ie 6 and 7 to get round cell border bug in these browsers when rendering empty cells   //===============================================================================   FormatText: function (sCellValue, oOptions, oRow) {      if ($.trim(sCellValue).length === 0)       return "&nbsp;";     else       return sCellValue;   },    //===============================================================================   // FormatLink function    //===============================================================================   FormatLink: function (sCellValue, oOptions, oRow) {      //Add link?     if ($.trim(sCellValue).length === 0)       return "&nbsp;";     else {        var sHref = oOptions.colModel.link.format(sCellValue),         sHtml = oRow[_oJQG.dColumns[oOptions.colModel.textfield]];        //If this is a javascript URL then make use of OnClick       return (oOptions.colModel.link.indexOf('javascript') !== -1)         ? "<a href="javascript:void(0);" onclick="" + sHref + "">" + sHtml + "</a>"         : "<a href="" + sHref + "">" + sHtml + "</a>";     }   },    //===============================================================================   // FormatPercent function    //===============================================================================   FormatPercent: function (sCellValue, oOptions, oRow) {      //Transform to percent     var dPercent = parseFloat(sCellValue) * 100;      //Return integer of float to strip off decimal places     return parseInt(dPercent, 10);   },    //===============================================================================   // FormatNumericTextBox function   //===============================================================================   FormatNumericTextBox: function (sCellValue, oOptions, oRow) {      //Use javascript regular expression to render localisation specific decimal separators (replacing ".")     var sLocalised = sCellValue.replace(".", oOptions.colModel.decimalSeparator);      //Render input textbox     return "<input type="text" id="NumericTextBox" + oOptions.rowId + "" value="" + sLocalised + "" class="txtControl" style="text-align:right;" />&nbsp;";   } }  //=============================================================================== // The functions below are callbacks and so sit outside _oJQG because  // they could not access _oJQG using the "this" keyword if they sat inside it //===============================================================================  //=============================================================================== // Get data for grid from AJAX call. NB This is a callback and so _oJQG. referred to directly rather than this. //=============================================================================== var GetJQGridData = function (oPostData) {    var sJSONData;   var $Grid = $('#' + _oJQG.sId);    //Show "Loading"   _oJQG.BeginGridRequest(_oJQG.sId);    //Get latest SearchOptions / sort settings   oPostData.dSearchOptions = _oJQG.GetSearchOptions(_oJQGProperties.aSearchOptionParameters);   oPostData.sSortField = $Grid.jqGrid('getGridParam', 'sortname');   oPostData.sSortOrder = $Grid.jqGrid('getGridParam', 'sortorder');    //If a call is in process then abort it before a new one is started   if (_oJQG.oRequest[_oJQGProperties.sURL] != null) {      try {       _oJQG.oRequest[_oJQGProperties.sURL].abort();     }     catch (e) {     }   }    //Currently there is a deserialization bug in MVC (http://connect.microsoft.com/VisualStudio/feedback/details/636647/make-jsonvalueproviderfactory-work-with-dictionary-types-in-asp-net-mvc)   //This is a workaround until it is fixed.   if (_oJQG.bIsMVC) {      // Deep copy     var oPostDataCopy = $.extend(true, {}, oPostData);      oPostDataCopy.dSearchOptions = JSON.stringify(oPostDataCopy.dSearchOptions);     oPostDataCopy.aOriginalColumnDefinition = JSON.stringify(oPostDataCopy.aOriginalColumnDefinition);     oPostDataCopy.aExtraDataColumns = JSON.stringify(oPostDataCopy.aExtraDataColumns);      sJSONData = JSON.stringify(oPostDataCopy);    }   else {      sJSONData = JSON.stringify(oPostData);   }    //Perform AJAX call and store jqXHR Object so it can be aborted later if necessary   _oJQG.oRequest[_oJQGProperties.sURL] = $.ajax(_oJQGProperties.sURL, //URL of WebService/PageMethod used to populate the grid     {       data: sJSONData, // For empty input data use "{}",       type: "POST",       contentType: "application/json",       complete: GetJQGridDataCallback     }); },  //=============================================================================== // Method triggered when the AJAX call completes.  NB This is a callback and so _oJQG. referred to directly rather than this. //=============================================================================== GetJQGridDataCallback =  function (oJSONData, sStatus) {    //Wipe the jqXHR object   _oJQG.oRequest[_oJQGProperties.sURL] = null;       if (sStatus === "abort") {     //Do nothing   }   else {          //Translate JSON response into object     var oData = JSON.parse(oJSONData.responseText);     var $Grid = $('#' + _oJQG.sId); //Get grid      if (sStatus === "success") {        var oGridData = ((_oJQG.bIsMVC)          ? oData         : oData.d //Data stored in "d" because of ASP.NET AJAX security measure         );        //Populate grid with data       $Grid[0].addJSONData(oGridData);        //Perform post load processing if a function is defined       if (_oJQG.fnPostLoadProcessing) _oJQG.fnPostLoadProcessing($Grid, oGridData);        //Maximise grid       if (_oJQG.fnResizePage) _oJQG.fnResizePage();        //Save grid definition       _oJQG.SaveGridDefinition($Grid, $Grid.jqGrid('getGridParam', 'sortname'), $Grid.jqGrid('getGridParam', 'sortorder'));        //Hide "Loading"       _oJQG.EndGridRequest(_oJQG.sId);     }     else {        //Common cause of timeouts is sort criteria - so switch to the defaults       if ((_oJQGProperties.sDefaultSortName != null) && (_oJQGProperties.sDefaultSortOrder != null)) {          //Set grid criteria back to defaults         $Grid.setGridParam({           sortname: _oJQGProperties.sDefaultSortName,           sortorder: _oJQGProperties.sDefaultSortOrder//,           //((_oJQGProperties.aPageSizes.length > 0) ? _oJQGProperties.aPageSizes[0] : 100) - COMMENTED OUT FOR NOW; BRING BACK IF SEEMS USEFUL         });          //Set grid search image         $Grid.sortGrid(_oJQGProperties.sDefaultSortName, false);       }        var sError = _oJQGProperties.sCaptionDefaultErrorMessage;       try {          //Extract user friendly error if available         if (oData.Message !== "There was an error processing the request.") sError = oData.Message;       }       catch (e) {       }        //Inform user of error       _oJQG.ShowMessage(sError)     }   } },  //=============================================================================== // What to do when sorting by column - event is called just after clicking to sort but before actual sorting... //=============================================================================== JQGridColumnSort = function (sSortName, iCol, sSortOrder) {    var sSortNameToUse = sSortName;    //If sSortName has been prefixed with the grid id (eg does it look like this: "grid_TradeDate") then chop off prefix   if (sSortName.indexOf(this.id + "_") !== -1) {     sSortNameToUse = sSortName.split("_")[1];   }    //Change the sort name / sort order variables   _oJQGUserSettings.sSortName = _oJQGUserSettings.aColModel[_oJQG.dColumns[sSortNameToUse]].index;   _oJQGUserSettings.sSortOrder = sSortOrder;    //Perform pre load processing if a function is defined   if (_oJQG.fnPreLoadProcessing) _oJQG.fnPreLoadProcessing(); }
